mapper.update:
UPDATE menu_info
SET name=#{name}, price=#{price}, one_sentence=#{one_sentence}, description=#{description}, img_url=#{img_url}, is_sold=#{is_sold}, category=#{category}
WHERE id=#{id}

mapper.select:
SELECT id, name, price, one_sentence, description, img_url, is_sold, category
FROM menu_info
WHERE id=#{id}
